import React, { useState, useEffect } from "react";
import { Link } from "react-scroll";
import styles from "./styles/image_landing.module.css";

const ImageLanding = () => {
  // List of background images
  const images = [
    "https://images.pexels.com/photos/8327700/pexels-photo-8327700.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1",
    "https://images.pexels.com/photos/7695415/pexels-photo-7695415.jpeg",
    "https://images.pexels.com/photos/7188804/pexels-photo-7188804.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1",
    "https://images.pexels.com/photos/3789194/pexels-photo-3789194.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1",
    // Add more image URLs here
  ];

  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  useEffect(() => {
    // Change background image every 5 seconds
    const interval = setInterval(() => {
      setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 5000);

    return () => clearInterval(interval);
  }, [images.length]);

  return (
    <React.Fragment>
      <div
        className={styles.backgroundImage}
        style={{ backgroundImage: `url(${images[currentImageIndex]})` }}
      >
        <div className={styles.title}>
          <p className={styles.header}> ScanVue</p>
          <p className={styles.text}>
            Empowering independence for those who see the world differently.
          </p>
        </div>
        <Link
          to="Features"
          activeClass="active"
          spy={true}
          smooth={true}
          offset={-70}
          duration={500}
        >
          <button className={`btn btn-primary p-3 ${styles.donateBtn}`}>
            Explore Features
          </button>
        </Link>
      </div>
    </React.Fragment>
  );
};

export default ImageLanding;
