import React, { useState } from "react";
import SpeechRecognition, { useSpeechRecognition } from "react-speech-recognition";
import { useHistory } from "react-router-dom";
import { useSpeechSynthesis } from "react-speech-kit";

const Assistant = () => {
  const history = useHistory();
  const { speak } = useSpeechSynthesis();
  const [listening, setListening] = useState(false);
  const { transcript } = useSpeechRecognition();

  const handleCommand = (link) => {
    const redirectMap = {
      "object detection": "/object-detection",
      "image to text": "/image-to-text",
      "text to speech": "/text-to-speech",
      "speech to text": "/speech-to-text",
      "home": "/",
    };

    const x = `Redirect to: ${link}`;
    console.log(x);

    if (redirectMap.hasOwnProperty(link)) {
      speak({ text: x });
      history.push(redirectMap[link]);
    } else {
      speak({ text: "I don't understand. Please try again!" });
    }
  };

  const startListening = () => {
    setListening(true);
    SpeechRecognition.startListening({ continuous: true, language: 'en-US' });
  };

  const stopListening = () => {
    setListening(false);
    SpeechRecognition.stopListening();
  };

  return (
    <div>
      <div className="row m-2 p-4" style={{ background: "#f5f5f5", textAlign: "center" }}>
        <h1 style={{ fontFamily: "Georgia, Times, serif", fontSize: "45px", fontWeight: "bolder" }}>Assistant</h1>
      </div>
      <div style={{ border: "10px solid gray", padding: "10px" }}>
        <div>Hi, welcome to Voice Assistant</div>
        <div style={{ margin: '10px 0' }}>
          {listening ? (
            <button onClick={stopListening}>Stop listening</button>
          ) : (
            <button onClick={startListening}>Start listening</button>
          )}
        </div>
        <div style={{ marginBottom: '20px' }}>Client said: {transcript ? transcript : "Start listening for transcript"}</div>
      </div>
    </div>
  );
};

export default Assistant;
