import React, { useState } from "react";
import FileUpload from "./FileUpload";
import { useSpeechSynthesis } from "react-speech-kit";

const ImageToText = () => {
  const [imageInputURL, setImageInputUrl] = useState("");
  const [imageOutputURL, setImageOutputUrl] = useState("");
  const [text, setText] = useState("");
  const { speak, cancel, speaking } = useSpeechSynthesis();

  const speakOutput = (text) => {
    speak({ text: `This Image contains: ${text}` });
  };

  return (
    <React.Fragment>
      <div className="container-fluid">
        <div className="row m-2 p-4" style={{ background: "#f5f5f5", textAlign: "center" }}>
          <h1 style={{ fontFamily: "Georgia, Times, serif", fontSize: "45px", fontWeight: "bolder" }}>
            Image To Text
          </h1>
        </div>
        <FileUpload
          setImageInputUrl={setImageInputUrl}
          setImageOutputUrl={setImageOutputUrl}
          setText={setText}
          speak={speakOutput}
          url={"http://localhost:5000/image-to-text"}
        />
        <div className="row justify-content-center">
          <div className="col-lg-6 p-2">
            {imageInputURL && (
              <div className="card">
                <h3 className="card-header text-center">Input Image</h3>
                <div className="card-body">
                  <img
                    className="img-fluid"
                    src={imageInputURL}
                    alt="Input"
                  />
                </div>
              </div>
            )}
          </div>
          <div className="col-lg-6 p-2">
            {imageOutputURL && (
              <div className="card">
                <h3 className="card-header text-center">Result</h3>
                <div className="card-body">
                  <img
                    className="img-fluid"
                    src={imageOutputURL}
                    alt="Output"
                  />
                </div>
              </div>
            )}
            {text && (
              <div className="card mt-3">
                <h3 className="card-header text-center">Image Contains</h3>
                <div className="card-body text-center">
                  <div className="p-3" style={{ background: "rgba(235, 255, 251, 0.8)" }}>
                    <h5>{text}</h5>
                  </div>
                </div>
                <div className="card-footer text-center">
                  {speaking ? (
                    <button className="btn btn-secondary m-2 p-2" type="button" onClick={cancel}>
                      Stop
                    </button>
                  ) : (
                    <button className="btn btn-success m-2 p-2" type="button" onClick={() => speak({ text })}>
                      Speak
                    </button>
                  )}
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </React.Fragment>
  );
};

export default ImageToText;
