import React, { useState } from "react";
import { useSpeechSynthesis } from "react-speech-kit";
import SpeechRecognition, {
  useSpeechRecognition,
} from "react-speech-recognition";

const SpeechToText = () => {
  const [notes, setNotes] = useState([
    { time: "1", val: "Hello Everyone" },
    { time: "2", val: "Welcome to Scanvue" },
  ]);

  const {
    transcript,
    listening,
    resetTranscript,
    browserSupportsSpeechRecognition,
  } = useSpeechRecognition();

  const { speak } = useSpeechSynthesis();

  const handleStartListening = () => {
    SpeechRecognition.startListening({ continuous: true });
  };

  const handleStopListening = () => {
    SpeechRecognition.abortListening();
  };

  if (!browserSupportsSpeechRecognition) {
    return <span>Browser doesn't support speech recognition.</span>;
  }

  return (
    <div className="phone-container">
      <div className="phone">
        <div className="phone-screen">
          <div className="assistant-container">
            <div className="assistant-content">
              <div className="assistant-title">
                <h1>Speech To Text</h1>
              </div>
              <div className="assistant-body">
                {!listening && (
                  <div className="message">
                    Please click "Start" and start speaking.
                  </div>
                )}
                {listening && (
                  <div className="message">Please click "Stop" to stop speaking.</div>
                )}
                <p>Microphone: {listening ? "on" : "off"}</p>
                {!listening && (
                  <button className="btn btn-success" onClick={handleStartListening}>
                    Start
                  </button>
                )}
                {listening && (
                  <button className="btn btn-secondary" onClick={handleStopListening}>
                    Stop
                  </button>
                )}
                <button className="btn btn-warning" onClick={resetTranscript}>
                  Reset
                </button>
                <button
                  className="btn btn-primary"
                  disabled={!transcript}
                  onClick={() => speak({ text: transcript })}
                >
                  Speak Transcription
                </button>
                <hr />
                {transcript && (
                  <div>
                    <h5 className="mx-3">You spoke:</h5>
                    <div className="transcript">{transcript}</div>
                    <button
                      className="btn btn-info mx-2"
                      onClick={() => {
                        const updatedNotes = [...notes];
                        updatedNotes.unshift({ time: Date.now(), val: transcript });
                        setNotes(updatedNotes);
                        resetTranscript();
                      }}
                    >
                      Save
                    </button>
                    <button
                      className="btn btn-info"
                      onClick={() => {
                        navigator.clipboard.writeText(transcript);
                        resetTranscript();
                      }}
                    >
                      Copy
                    </button>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
      <style jsx>{`
        .phone-container {
          display: flex;
          justify-content: center;
          align-items: center;
          height: 100vh;
          background: linear-gradient(135deg, #2d75ff, #a8bfff);
          overflow: hidden;
        }

        .phone {
          width: 60%; /* Adjust as needed */
          height: 90%; /* Adjust as needed */
          max-width: 600px; /* Set maximum width */
          max-height: 800px; /* Set maximum height */
          background-color: #333;
          border-radius: 20px;
          overflow: hidden;
          box-shadow: 0 0 20px rgba(0, 0, 0, 0.5);
        }

        .phone-screen {
          height: 100%;
          overflow-y: auto;
          padding: 20px;
          display: flex;
          flex-direction: column;
          justify-content: center;
          align-items: center;
          color: #fff;
        }

        .assistant-container {
          width: 100%;
          max-width: 400px;
        }

        .assistant-content {
          text-align: center;
        }

        .assistant-title {
          font-size: 28px;
          margin-bottom: 20px;
        }

        .assistant-body {
          text-align: center;
          margin-top: 20px;
        }

        .message {
          margin: 10px 0;
        }

        .transcript {
          margin: 4px;
          padding: 10px;
          border: 2px solid #f8f9fa;
          border-radius: 10px;
          background: rgba(191, 191, 191, 0.7);
          min-height: 90px;
        }

        .btn {
          margin: 10px;
        }
      `}</style>
    </div>
  );
};

export default SpeechToText;
