import React, { useState } from "react";
import SpeechRecognition, {
  useSpeechRecognition,
} from "react-speech-recognition";
import { useHistory } from "react-router-dom";

import { useSpeechSynthesis } from "react-speech-kit";

const Assistant = () => {
  const history = useHistory();
  const { speak } = useSpeechSynthesis();
  const [isListening, setIsListening] = useState(false);

  const startListening = () => {
    setIsListening(true);
    SpeechRecognition.startListening({ continuous: true });
  };

  const stopListening = () => {
    setIsListening(false);
    SpeechRecognition.stopListening();
  };

  const commands = [
    {
      command: "Go to *",
      callback: (link) => {
        const x = `Redirect to: ${link}`;
        console.log(x);
        if (link === "object detection") {
          speak({ text: x });
          history.push(`/object-detection`);
        } else if (link === "face recognition") {
          speak({ text: x });
          history.push(`/face-recognition`);
        } else if (link === "image to text") {
          speak({ text: x });
          history.push(`/image-to-text`);
        } else if (link === "text to speech") {
          speak({ text: x });
          history.push(`/text-to-speech`);
        } else if (link === "speech to text") {
          speak({ text: x });
          history.push(`/speech-to-text`);
        } else if (link === "home") {
          speak({ text: x });
          history.push(`/`);
        } else {
          speak({ text: "I don't get it, Please try again!" });
        }
      },
    },
    {
      command: "Hey Jarvis",
      callback: () => {
        speak({ text: "Hi Sir" });
        console.log("hii");
      },
    },
  ];
  const { transcript: t1 } = useSpeechRecognition({ commands });

  return (
    <div className="phone-container">
      <div className="phone">
        <div className="phone-speaker"></div>
        <div className="phone-screen">
          <div className="assistant-container">
            <div className="assistant-content">
              <div className="assistant-title">
                <h1>Assistant</h1>
              </div>
              <div className="assistant-body">
                <h3>Say "Hey Jarvis"</h3>
                <p>{t1 ? t1 : "Start listening for transcript"}</p>
                <button
                  onClick={isListening ? stopListening : startListening}
                  className={`listening-btn ${isListening ? "listening" : ""}`}
                >
                  {isListening ? "Listening" : "Start Listening"}
                </button>
              </div>
            </div>
          </div>
        </div>
        <div className="phone-btns">
          <div className="phone-btn phone-btn-red"></div>
          <div className="phone-btn phone-btn-green"></div>
          <div className="phone-btn phone-btn-blue"></div>
        </div>
      </div>
      <style jsx>{`
        .phone-container {
          display: flex;
          justify-content: center;
          align-items: center;
          height: 100vh;
          background: linear-gradient(135deg, #2d75ff, #a8bfff);
        }
        .phone {
          position: relative;
          width: 300px;
          height: 600px;
          background: #000;
          border-radius: 20px;
          overflow: hidden;
          box-shadow: 0 0 20px rgba(0, 0, 0, 0.5);
        }
        .phone-speaker {
          position: absolute;
          top: 20px;
          left: 50%;
          transform: translateX(-50%);
          width: 50px;
          height: 5px;
          background: #666;
          border-radius: 50px;
        }
        .phone-screen {
          position: relative;
          width: 100%;
          height: 80%;
          background: #fff;
          overflow-y: auto;
          padding: 20px;
        }
        .assistant-container {
          display: flex;
          justify-content: center;
          align-items: center;
          height: 100%;
        }
        .assistant-content {
          text-align: center;
        }
        .assistant-title {
          font-family: Georgia, Times, serif;
          font-size: 32px;
          font-weight: bolder;
          margin-bottom: 20px;
        }
        .assistant-body {
          margin-top: 20px;
        }
        .listening-btn {
          background-color: #f00;
          color: #fff;
          border: none;
          border-radius: 5px;
          padding: 10px;
          cursor: pointer;
          transition: background-color 0.3s ease;
        }
        .listening-btn.listening {
          background-color: #00f;
        }
        .phone-btns {
          position: absolute;
          bottom: 10px;
          left: 50%;
          transform: translateX(-50%);
          display: flex;
        }

      `}</style>
    </div>
  );
};

export default Assistant;
