import React, { useState } from "react";
import Notes from "./notes";
import SpeechRecognition, {
  useSpeechRecognition,
} from "react-speech-recognition";
import { useSpeechSynthesis } from "react-speech-kit";

const SpeechToText = () => {
  const [notes, setNotes] = useState([
    { time: "1", val: "Hello Everyone" },
    { time: "2", val: "Welcome to Scanvue" },
  ]);

  const {
    transcript,
    listening,
    resetTranscript,
    browserSupportsSpeechRecognition,
  } = useSpeechRecognition();

  const { speak } = useSpeechSynthesis();

  const handleStartListening = () => {
    SpeechRecognition.startListening({ continuous: true });
  };

  const handleStopListening = () => {
    SpeechRecognition.abortListening();
  };

  if (!browserSupportsSpeechRecognition) {
    return <span>Browser doesn't support speech recognition.</span>;
  }

  return (
    <div className="phone-container">
      <div className="phone">
        <div className="phone-screen">
          <div className="phone-title">Speech To Text</div>
          <div className="phone-content">
            {!listening && (
              <div className="message">
                Please click "Start" and start speaking.
              </div>
            )}
            {listening && (
              <div className="message">Please click "Stop" to stop speaking.</div>
            )}
            <p>Microphone: {listening ? "on" : "off"}</p>
            {!listening && (
              <button className="btn btn-success" onClick={handleStartListening}>
                Start
              </button>
            )}
            {listening && (
              <button className="btn btn-secondary" onClick={handleStopListening}>
                Stop
              </button>
            )}
            <button className="btn btn-warning" onClick={resetTranscript}>
              Reset
            </button>
            <button
              className="btn btn-primary"
              disabled={!transcript}
              onClick={() => speak({ text: transcript })}
            >
              Speak Transcription
            </button>
            <hr />
            {transcript && (
              <div>
                <h5 className="mx-3">You spoke:</h5>
                <div className="transcript">{transcript}</div>
                <button
                  className="btn btn-info mx-2"
                  onClick={() => {
                    const updatedNotes = [...notes];
                    updatedNotes.unshift({ time: Date.now(), val: transcript });
                    setNotes(updatedNotes);
                    resetTranscript();
                  }}
                >
                  Save
                </button>
                <button
                  className="btn btn-info"
                  onClick={() => {
                    navigator.clipboard.writeText(transcript);
                    resetTranscript();
                  }}
                >
                  Copy
                </button>
              </div>
            )}
          </div>
        </div>
      </div>

      <style jsx>{`
        .phone-container {
          display: flex;
          justify-content: center;
          align-items: center;
          height: calc(100vh - 40px); /* Subtracting 40px for the footer space */
          background: linear-gradient(135deg, #2d75ff, #a8bfff);
        }

        .phone {
          width: 70vw; /* Adjusted width */
          max-width: 450px; /* Maximum width */
          height: calc(70vw * 1.77); /* 9:16 aspect ratio */
          background-color: #fff;
          border-radius: 20px;
          overflow: hidden;
        }

        .phone-screen {
          border-radius: 20px;
          height: 100%;
          overflow-y: auto;
          padding: 20px;
          display: flex;
          flex-direction: column;
          justify-content: center;
          align-items: center;
          color: #000;
        }

        .phone-title {
          font-family: Arial, sans-serif;
          font-size: 24px;
          font-weight: bold;
          margin-bottom: 20px;
        }

        .phone-content {
          text-align: center;
        }

        .message {
          margin: 10px 0;
        }

        .transcript {
          margin: 4px;
          padding: 10px;
          border: 2px solid #f8f9fa;
          border-radius: 10px;
          background: rgba(191, 191, 191, 0.7);
          min-height: 90px;
        }

        .btn {
          margin: 10px;
        }
      `}</style>
    </div>
  );
};

export default SpeechToText;
