  const [notes, setNotes] = useState([
    { time: "1", val: "Hello Everyone" },
    { time: "2", val: "Welcome to Scanvue" },
  ]);

  const {
    transcript,
    listening,
    resetTranscript,
    browserSupportsSpeechRecognition,
  } = useSpeechRecognition();

  const handleStartListening = () => {
    SpeechRecognition.startListening({ continuous: true });
  };

  const handleStopListening = () => {
    SpeechRecognition.abortListening();
  };

  if (!browserSupportsSpeechRecognition) {
    return <span>Browser doesn't support speech recognition.</span>;
  }

  return (
    <div className="phone-container" style={{ overflowX: "hidden" }}>
      <div className="phone">
        <div className="phone-screen">
          <div className="phone-title">Speech To Text</div>
          <div className="phone-content">
            {!listening && (
              <div className="message">
                Please click "Start" and start speaking.
              </div>
            )}
            {listening && (
              <div className="message">Please click "Stop" to stop speaking.</div>
            )}
            <p>Microphone: {listening ? "on" : "off"}</p>
            {!listening && (
              <button className="btn btn-success" onClick={handleStartListening}>
                Start
              </button>
            )}
            {listening && (
              <button className="btn btn-secondary" onClick={handleStopListening}>
                Stop
              </button>
            )}
            <button className="btn btn-warning" onClick={resetTranscript}>
              Reset
            </button>
            <hr />
            {transcript && (
              <div>
                <h5 className="mx-3">You speak:</h5>
                <div className="transcript">{transcript}</div>
                <button
                  className="btn btn-info mx-2"
                  onClick={() => {
                    const updatedNotes = [...notes];
                    updatedNotes.unshift({ time: Date.now(), val: transcript });
                    setNotes(updatedNotes);
                    resetTranscript();
                  }}
                >
                  Save
                </button>
                <button
                  className="btn btn-info"
                  onClick={() => {
                    navigator.clipboard.writeText(transcript);
                    resetTranscript();
                  }}
                >
                  Copy
                </button>
              </div>
            )}
          </div>
        </div>
      </div>

      <style jsx>{`
        .phone-container {
          display: flex;
          justify-content: center;
          align-items: center;
          height: 100vh;
          background: linear-gradient(135deg, #fff, #f5f5f5);
          overflow-x: hidden;
        }

        .phone {
          background-color: #fff;
          border-radius: 40px;
          overflow: hidden;
          max-width: 100%;
        }

        .phone-screen {
          border-radius: 30px;
          height: 500px;
          overflow-y: auto;
          padding: 20px;
          display: flex;
          flex-direction: column;
          justify-content: center;
          align-items: center;
          color: #000; /* Change text color to black */
        }

        .phone-title {
          font-family: Arial, sans-serif;
          font-size: 24px;
          font-weight: bold;
          margin-bottom: 20px;
        }

        .phone-content {
          text-align: center;
        }

        .message {
          margin: 10px 0;
        }

        .transcript {
          margin: 4px;
          padding: 10px;
          border: 2px solid #f8f9fa;
          border-radius: 10px;
          background: rgba(0, 0, 0, 0.1); /* Change background color to light gray */
          min-height: 90px;
        }

        .btn {
          margin: 10px;
        }
      `}</style>
    </div>
  );
};

export default SpeechToText;
