import React, { useState } from "react";
import FileUpload from "./FileUpload";
import { useSpeechSynthesis } from "react-speech-kit";

const ImageToText = () => {
  const [imageInputURL, setImageInputUrl] = useState("");
  const [imageOutputURL, setImageOutputUrl] = useState("");
  const [text, setText] = useState("");
  const { speak, cancel, speaking } = useSpeechSynthesis();

  const speakOutput = (text) => {
    speak({ text: `This Image contains: ${text}` });
  };

  return (
    <React.Fragment>
      <div className="phone-container">
        <div className="phone">
          <div className="phone-screen">
            <div className="assistant-container">
              <div className="assistant-content">
                <div className="assistant-title">
                  <h1>Image To Text</h1>
                </div>
                <div className="assistant-body">
                  <FileUpload
                    setImageInputUrl={setImageInputUrl}
                    setImageOutputUrl={setImageOutputUrl}
                    setText={setText}
                    speak={speakOutput}
                    url={"http://localhost:5000/image-to-text"}
                  />
                  <div className="container border my-5">
                    <div className="row" style={{ textAlign: "center" }}>
                      {imageInputURL && (
                        <div className="col-md-6 p-2">
                          <h3 className="display-5">Input Image</h3>
                          <img
                            style={{
                              width: "100%",
                              border: "4px solid #d2d2d2",
                              borderRadius: "5px",
                            }}
                            src={imageInputURL}
                            alt="img"
                          />
                        </div>
                      )}
                      {imageOutputURL && (
                        <div className="col-md-6 p-2">
                          <h3 className="display-5">Result</h3>
                          <img
                            style={{
                              width: "100%",
                              border: "4px solid #d2d2d2",
                              borderRadius: "5px",
                            }}
                            src={imageOutputURL}
                            alt="img"
                          />
                        </div>
                      )}
                    </div>
                    {text && (
                      <div
                        className="row"
                        style={{
                          marginTop: "30px",
                          borderTop: "2px solid gray",
                        }}
                      >
                        <hr />
                        <h1>
                          Image Contains:
                          {speaking ? (
                            <button
                              className="btn btn-secondary m-2 p-2"
                              type="button"
                              onClick={cancel}
                            >
                              Stop
                            </button>
                          ) : (
                            <button
                              className="btn btn-success m-2 p-2"
                              type="button"
                              onClick={() => speak({ text })}
                            >
                              Speak
                            </button>
                          )}
                        </h1>
                        <hr />
                        <div
                          className="p-5 m-5"
                          style={{
                            background: "rgba(235, 255, 251, 0.8)",
                            width: "85%",
                          }}
                        >
                          <h5>{text}</h5>
                        </div>
                      </div>
                    )}
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      <style jsx>{`
        .phone-container {
          display: flex;
          justify-content: center;
          align-items: center;
          height: 100vh;
          background: linear-gradient(135deg, #2d75ff, #a8bfff);
        }
        .phone {
          width: 60%; /* Adjust as needed */
          height: 90%; /* Adjust as needed */
          max-width: 600px; /* Set maximum width */
          max-height: 800px; /* Set maximum height */
          background: #333;
          border-radius: 20px;
          overflow: hidden;
          box-shadow: 0 0 20px rgba(0, 0, 0, 0.5);
        }
        .phone-screen {
          width: 100%;
          height: 100%;
          background: #fff;
          padding: 20px;
          display: flex;
          justify-content: center;
          align-items: center;
        }
        .assistant-container {
          width: 100%;
          max-width: 400px;
        }
        .assistant-content {
          text-align: center;
        }
        .assistant-title {
          font-size: 28px;
          margin-bottom: 20px;
        }
        .settings-container {
          margin-bottom: 20px;
        }
        .voice-select {
          margin-bottom: 10px;
        }
        .rate-pitch-sliders {
          display: flex;
          justify-content: space-between;
          align-items: center;
          margin-bottom: 20px;
        }
        .rate-slider,
        .pitch-slider {
          width: 45%;
        }
        .rate-slider span,
        .pitch-slider span {
          margin-left: 10px;
        }
        .text-area {
          margin-bottom: 20px;
        }
        .text-area textarea {
          width: 100%;
          height: 100px;
          resize: none;
          padding: 10px;
          font-size: 16px;
          border-radius: 5px;
          border: 1px solid #ccc;
        }
        .button-container {
          display: flex;
          justify-content: center;
        }
        .speak-button {
          padding: 10px 20px;
          font-size: 16px;
          border: none;
          border-radius: 5px;
          cursor: pointer;
          transition: background-color 0.3s ease;
        }
        .speak-button:hover {
          background-color: #4caf50;
        }
        .speak-button.speaking {
          background-color: #f44336;
        }
      `}</style>
    </React.Fragment>
  );
};

export default ImageToText;
