  };

  const handleVoiceCommand = (command) => {
    const x = `Redirect to: ${command}`;
    console.log(x);
    switch (command.toLowerCase()) {
      case "object detection":
        speak({ text: x });
        history.push(`/object-detection`);
        break;
      case "face recognition":
        speak({ text: x });
        history.push(`/face-recognition`);
        break;
      case "image to text":
        speak({ text: x });
        history.push(`/image-to-text`);
        break;
      case "text to speech":
        speak({ text: x });
        history.push(`/text-to-speech`);
        break;
      case "speech to text":
        speak({ text: x });
        history.push(`/speech-to-text`);
        break;
      case "home":
        speak({ text: x });
        history.push(`/`);
        break;
      case "hey jarvis":
        speak({ text: "Hi Sir" });
        console.log("Hi");
        break;
      default:
        speak({ text: "I don't understand, please try again!" });
        break;
    }
  };

  return (
