import React, { useState } from "react";
import { Link } from "react-router-dom";
import styles from "./styles/navbar.module.css";

const Navbar = () => {
  const [showFeature, setShowFeature] = useState(false);

  return (
    <React.Fragment>
      <div className={styles.bodyPadding}></div>
      <nav className={`navbar navbar-expand-md sticky-top ${styles.navbar}`}>
        <li className={`navbar-brand ${styles.title}`}>
          <Link className={styles.brand} to="/">
            ScanVue
          </Link>
        </li>

        <button
          className="navbar-toggler"
          type="button"
          data-toggle="collapse"
          data-target="#navbarToggler"
          aria-controls="navbarToggler"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className={`navbar-toggler-icon ${styles.toggleIcon}`}></span>
        </button>

        <div className="collapse navbar-collapse" id="navbarToggler">
          <ul className={`navbar-nav ${styles.navLinks}`}>
            <li>
              <Link
                to="/"
                className={`${styles.navLink} ${showFeature && styles.active}`}
                onClick={(ev) => {
                  ev.preventDefault();
                  setShowFeature(!showFeature);
                }}
              >
                FEATURES
              </Link>
              {showFeature && (
                <ul className={`dropdown-menu ${styles.dropdownMenu}`}>
                  <li>
                    <Link
                      className={`${styles.dropdownItem}`}
                      to="/object-detection"
                      onClick={() => {
                        setShowFeature(false);
                      }}
                    >
                      Object Detection
                    </Link>
                  </li>
                  <li>
                    <Link
                      className={`${styles.dropdownItem}`}
                      to="/face-recognition"
                      onClick={() => {
                        setShowFeature(false);
                      }}
                    >
                      Face Recognition
                    </Link>
                  </li>
                  <li>
                    <Link
                      className={`${styles.dropdownItem}`}
                      to="/image-to-text"
                      onClick={() => {
                        setShowFeature(false);
                      }}
                    >
                      Image To Text
                    </Link>
                  </li>
                </ul>
              )}
            </li>

            <li>
              <Link
                to="/about-us"
                className={`${styles.navLink} ${styles.aboutLink}`}
              >
                ABOUT ME
              </Link>
            </li>
          </ul>
        </div>
      </nav>
    </React.Fragment>
  );
};

export default Navbar;
