import React, { useState, useEffect } from "react";
import { Link } from "react-scroll";
import styles from "./styles/image_landing.module.css";

const ImageLanding = () => {
  // List of background images
  const images = [
    "https://media.istockphoto.com/id/1452457786/photo/young-blind-man-with-smartphone-sitting-on-bench-in-park-in-city-calling.jpg?s=2048x2048&w=is&k=20&c=nx8GVBa7ue7Y6poLBL89YXEbFv0Jk1clZTVLFuN6G1E=",
    "https://via.placeholder.com/1920x1080?text=Image+2",
    "https://via.placeholder.com/1920x1080?text=Image+3",
    // Add more image URLs here
  ];

  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  useEffect(() => {
    // Change background image every 5 seconds
    const interval = setInterval(() => {
      setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 5000);

    return () => clearInterval(interval);
  }, [images.length]);

  return (
    <React.Fragment>
      <div
        className={styles.backgroundImage}
        style={{ backgroundImage: `url(${images[currentImageIndex]})` }}
      >
        <div className={styles.title}>
          <p className={styles.header}> ScanVue</p>
          <p className={styles.text}>
            Empowering independence for those who see the world differently.
          </p>
        </div>
        <Link
          to="Features"
          activeClass="active"
          spy={true}
          smooth={true}
          offset={-70}
          duration={500}
        >
          <button className={`btn btn-primary p-3 ${styles.donateBtn}`}>
            Explore Features
          </button>
        </Link>
      </div>
    </React.Fragment>
  );
};

export default ImageLanding;
