import React, { useState, useEffect } from "react";
import SpeechRecognition, {
  useSpeechRecognition,
} from "react-speech-recognition";
import { useHistory } from "react-router-dom";
import { useSpeechSynthesis } from "react-speech-kit";

const Assistant = () => {
  const history = useHistory();
  const { speak } = useSpeechSynthesis();
  const [isListening, setIsListening] = useState(false);
  const { transcript: t1, resetTranscript } = useSpeechRecognition();

  useEffect(() => {
    // Start listening when component mounts
    startListening();
    return () => {
      // Clean up: stop listening when component unmounts
      stopListening();
    };
  }, []);

  useEffect(() => {
    handleCommands(t1);
  }, [t1]);

  const startListening = () => {
    setIsListening(true);
    SpeechRecognition.startListening({ continuous: true });
  };

  const stopListening = () => {
    setIsListening(false);
    SpeechRecognition.stopListening();
  };

  const handleCommands = (transcript) => {
    if (transcript) {
      const command = transcript.toLowerCase();
      if (command === "jarvis") {
        speak({ text: "Hi Sir" });
        console.log("Hi Sir");
        resetTranscript(); // Reset transcript after executing command
      } else if (command.startsWith("go to ")) {
        const link = command.substring(6);
        const x = `Redirect to: ${link}`;
        console.log(x);
        speak({ text: x });
        switch (link) {
          case "object detection":
            history.push(`/object-detection`);
            break;
          case "face recognition":
            history.push(`/face-recognition`);
            break;
          case "image to text":
            history.push(`/image-to-text`);
            break;
          case "text to speech":
            history.push(`/text-to-speech`);
            break;
          case "speech to text":
            history.push(`/speech-to-text`);
            break;
          case "home":
            history.push(`/`);
            break;
          default:
            speak({ text: "I don't get it, Please try again!" });
        }
        resetTranscript(); // Reset transcript after executing command
        stopListening(); // Stop listening after command is executed
      }
    }
  };

  return (
    <div className="phone-container">
      <div className="phone">
        <div className="phone-screen">
          <div className="assistant-container">
            <div className="assistant-content">
              <div className="assistant-title">
                <h1>Assistant</h1>
              </div>
              <div className="assistant-body">
                <h3>Say "Jarvis"</h3>
                <p>{t1 ? t1 : "Start listening for transcript"}</p>
                <button
                  onClick={isListening ? stopListening : startListening}
                  className={`listening-btn ${isListening ? "listening" : ""}`}
                >
                  {isListening ? "Listening" : "Start Listening"}
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
      <style jsx>{`
        /* Your existing styling */
      `}</style>
    </div>
  );
};

export default Assistant;
