import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import { useSpeechSynthesis } from "react-speech-kit";
import { OpenAI } from "openai";

const Assistant = () => {
  const history = useHistory();
  const { speak } = useSpeechSynthesis();
  const [listening, setListening] = useState(false);
  const [userInput, setUserInput] = useState("");
  const [assistantResponse, setAssistantResponse] = useState("");
  const openai = new OpenAI(process.env.OPENAI_API_KEY);

  const handleCommand = async () => {
    if (userInput.toLowerCase() === "hello") {
      speak({ text: "Hi Adarsh" });
      setAssistantResponse("Hi Adarsh");
    } else {
      const response = await openai.complete({
        engine: "davinci",
        prompt: userInput,
        maxTokens: 100,
      });
      speak({ text: response.choices[0].text });
      setAssistantResponse(response.choices[0].text);
    }
  };

  const startListening = () => {
    setListening(true);
  };

  const stopListening = () => {
    setListening(false);
    handleCommand();
  };

  return (
    <div>
      <div className="row m-2 p-4" style={{ background: "#f5f5f5", textAlign: "center" }}>
        <h1 style={{ fontFamily: "Georgia, Times, serif", fontSize: "45px", fontWeight: "bolder" }}>Assistant</h1>
      </div>
      <div style={{ border: "10px solid gray", padding: "10px" }}>
        <div>Hi, welcome to Voice Assistant</div>
        <div style={{ margin: '10px 0' }}>
          {listening ? (
            <button onClick={stopListening}>Stop listening</button>
          ) : (
            <button onClick={startListening}>Start listening</button>
          )}
        </div>
        <div style={{ marginBottom: '20px' }}>
          <p>Client said:</p>
          <input
            type="text"
            style={{ width: '100%', padding: '10px', fontSize: '16px' }}
            value={userInput}
            onChange={(e) => setUserInput(e.target.value)}
          />
        </div>
        <div>
          <p>Assistant Response:</p>
          <input
            type="text"
            style={{ width: '100%', padding: '10px', fontSize: '16px' }}
            value={assistantResponse}
            readOnly
          />
        </div>
      </div>
    </div>
  );
};

export default Assistant;
