import React, { useState } from "react";
import SpeechRecognition, { useSpeechRecognition } from "react-speech-recognition";
import { useHistory } from "react-router-dom";
import { useSpeechSynthesis } from "react-speech-kit";

const Assistant = () => {
  const history = useHistory();
  const { speak } = useSpeechSynthesis();
  const [listening, setListening] = useState(false);
  const [transcript, setTranscript] = useState("");

  const commands = [
    {
      command: "Go to *",
      callback: (link) => {
        const x = `Redirect to: ${link}`;
        console.log(x);
        if (link === "object detection") {
          speak({ text: x });
          history.push(`/object-detection`);
        } else if (link === "image to text") {
          speak({ text: x });
          history.push(`/image-to-text`);
        } else if (link === "text to speech") {
          speak({ text: x });
          history.push(`/text-to-speech`);
        } else if (link === "speech to text") {
          speak({ text: x });
          history.push(`/speech-to-text`);
        } else if (link === "home") {
          speak({ text: x });
          history.push(`/`);
        } else {
          speak({ text: "I don't get it, Please try again!" });
        }
      },
    },
    {
      command: "Hello",
      callback: () => {
        speak({ text: "Hello Adarsh" });
        console.log("hii");
      },
    },
  ];

  const { transcript: t1 } = useSpeechRecognition({ commands });

  const toggleListening = () => {
    if (listening) {
      SpeechRecognition.stopListening();
    } else {
      setTranscript(""); // Clear any existing transcript
      SpeechRecognition.startListening({ continuous: true, language: 'en-US' });
    }
    setListening(prev => !prev);
  };

  return (
    <div>
      <div className="row m-2 p-4" style={{ background: "#f5f5f5", textAlign: "center" }}>
        <h1 style={{ fontFamily: "Georgia, Times, serif", fontSize: "45px", fontWeight: "bolder" }}>Assistant</h1>
      </div>
      <div style={{ border: "10px solid gray", padding: "10px" }}>
        <div>Hi, welcome to Voice Assistant</div>
        <div style={{ marginBottom: '20px' }}>
          <input
            type="text"
            style={{ width: '100%', padding: '10px', fontSize: '16px' }}
            value={transcript ? transcript : "Assistant is listening for transcript"}
            readOnly
          />
        </div>
        <button onClick={toggleListening}>
          {listening ? "Stop listening" : "Start listening"}
        </button>
      </div>
    </div>
  );
};

export default Assistant;
