import React, { useState, useEffect } from "react";
import { Link } from "react-scroll";

const ImageLanding = () => {
  // List of background images
  const images = [
    "https://i.pcmag.com/imagery/articles/007OEmxN7mGIsAUtQFSIa2g-2..v1675708680.jpg",
    "https://img.freepik.com/premium-photo/young-blinded-man-using-phone-sending-voice-message_255667-29371.jpg",
    "https://images.pexels.com/photos/7695415/pexels-photo-7695415.jpeg",
    "https://images.pexels.com/photos/7188804/pexels-photo-7188804.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1",
    "https://images.pexels.com/photos/8327775/pexels-photo-8327775.jpeg?auto=compress&cs=tinysrgb&w=1260&h=750&dpr=1",
    // Add more image URLs here
  ];

  const [currentImageIndex, setCurrentImageIndex] = useState(0);

  useEffect(() => {
    // Change background image every 5 seconds
    const interval = setInterval(() => {
      setCurrentImageIndex((prevIndex) => (prevIndex + 1) % images.length);
    }, 5000);

    return () => clearInterval(interval);
  }, [images.length]);

  return (
    <React.Fragment>
      <div
        className="backgroundImage"
        style={{ backgroundImage: `url(${images[currentImageIndex]})` }}
      >
        <div className="content">
          <h1 className="title">ScanVue</h1>
          <p className="subtitle">
            Empowering independence for those who see the world differently.
          </p>
          <Link
            to="Features"
            activeClass="active"
            spy={true}
            smooth={true}
            offset={-70}
            duration={500}
          >
            <button className="btn exploreBtn">Explore Features</button>
          </Link>
        </div>
      </div>

      <style jsx>{`
        .backgroundImage {
          background-size: cover;
          background-position: center;
          height: 100vh;
          display: flex;
          justify-content: center;
          align-items: center;
        }

        .content {
          text-align: center;
          color: white;
        }

        .title {
          font-size: 4rem;
          font-weight: bold;
          margin-bottom: 1rem;
        }

        .subtitle {
          font-size: 1.5rem;
          margin-bottom: 2rem;
        }

        .exploreBtn {
          font-size: 1.2rem;
          padding: 1.5rem 2.5rem;
          background-color: #4caf50;
          color: white;
          border: none;
          border-radius: 30px;
          cursor: pointer;
          transition: background-color 0.3s;
        }

        .exploreBtn:hover {
          background-color: #388e3c;
        }
      `}</style>
    </React.Fragment>
  );
};

export default ImageLanding;
