import React from "react";
import SpeechRecognition, {
  useSpeechRecognition,
} from "react-speech-recognition";
import { useHistory } from "react-router-dom";
import { useSpeechSynthesis } from "react-speech-kit";

const Assistant = () => {
  const history = useHistory();
  const { speak } = useSpeechSynthesis();
  const { transcript: t1 } = useSpeechRecognition();

  const handleStartListening = () => {
    SpeechRecognition.startListening({ continuous: true });
  };

  const handleStopListening = () => {
    SpeechRecognition.stopListening();
  };

  const commands = [
    {
      command: "Go to *",
      callback: (link) => {
        const x = `Redirect to: ${link}`;
        console.log(x);
        if (link === "object detection") {
          speak({ text: x });
          history.push(`/object-detection`);
        } else if (link === "face recognition") {
          speak({ text: x });
          history.push(`/face-recognition`);
        } else if (link === "image to text") {
          speak({ text: x });
          history.push(`/image-to-text`);
        } else if (link === "text to speech") {
          speak({ text: x });
          history.push(`/text-to-speech`);
        } else if (link === "speech to text") {
          speak({ text: x });
          history.push(`/speech-to-text`);
        } else if (link === "home") {
          speak({ text: x });
          history.push(`/`);
        } else {
          speak({ text: "I don't get it, Please try again!" });
        }
      },
    },
    {
      command: "Hey Jarvis",
      callback: () => {
        speak({ text: "Hi Sir" });
        console.log("Hi");
      },
    },
  ];

  return (
    <div className="phone-container">
      <div className="phone">
        <div className="phone-screen">
          <div className="phone-title">Assistant</div>
          <div className="phone-content">
            <h3>Say "Hey Jarvis"</h3>
            <p>{t1 ? t1 : "Start listening for transcript"}</p>
            <button className="btn btn-primary" onClick={handleStartListening}>
              Start listening
            </button>
            <button className="btn btn-danger" onClick={handleStopListening}>
              Stop listening
            </button>
          </div>
        </div>
      </div>

      <style jsx>{`
        .phone-container {
          display: flex;
          justify-content: center;
          align-items: center;
          height: 100vh;
        }

        .phone {
          background-color: #000;
          border-radius: 40px;
          overflow: hidden;
          width: 300px;
        }

        .phone-screen {
          background: linear-gradient(135deg, #2d75ff, #a8bfff);
          border-radius: 30px;
          height: 500px;
          overflow-y: auto;
          padding: 20px;
          display: flex;
          flex-direction: column;
          justify-content: center;
          align-items: center;
        }

        .phone-title {
          font-family: Arial, sans-serif;
          font-size: 24px;
          font-weight: bold;
          color: #fff;
          margin-bottom: 20px;
        }

        .phone-content {
          text-align: center;
          color: #fff;
        }

        .btn {
          margin: 10px;
        }
      `}</style>
    </div>
  );
};

export default Assistant;
