import React, { useState } from "react";
import { useSpeechSynthesis } from "react-speech-kit";

const TextToSpeech = () => {
  const [text, setText] = useState("");
  const [pitch, setPitch] = useState(1);
  const [rate, setRate] = useState(1);
  const [voiceIndex, setVoiceIndex] = useState(null);
  const onEnd = () => {
    // You could do something here after speaking has finished
  };
  const { speak, cancel, speaking, supported, voices } = useSpeechSynthesis({
    onEnd,
  });

  const voice = voices[voiceIndex] || null;

  const styleFlexRow = { display: "flex", flexDirection: "row" };
  const styleContainerRatePitch = {
    display: "flex",
    flexDirection: "column",
    marginBottom: 12,
  };

  return (
    <div className="phone-container">
      <div className="phone">
        <div className="phone-screen">
          <div className="assistant-container">
            <div className="assistant-content">
              <div className="assistant-title">
                <h1>Text to Speech</h1>
              </div>
              <div className="assistant-body">
                {!supported && (
                  <p>
                    Oh no, it looks like your browser doesn&#39;t support
                    Speech Synthesis.
                  </p>
                )}
                {supported && (
                  <React.Fragment>
                    <div className="column">
                      <div
                        className="row m-2 p-2"
                        style={{ textAlign: "center" }}
                      >
                        <p>
                          {`Type a message below then click 'Speak'
                          and SpeechSynthesis will read it out.`}
                        </p>
                        <div style={{ display: "none" }}>
                          <label htmlFor="voice">Voice</label>
                          <select
                            id="voice"
                            name="voice"
                            value={voiceIndex || ""}
                            onChange={(event) => {
                              setVoiceIndex(event.target.value);
                            }}
                          >
                            <option value="">Default</option>
                            {voices.map((option, index) => (
                              <option key={option.voiceURI} value={index}>
                                {`${option.lang} - ${option.name}`}
                              </option>
                            ))}
                          </select>
                        </div>
                        <div className="col-9 row">
                          <div
                            className="col-6 px-5"
                            style={styleContainerRatePitch}
                          >
                            <div style={styleFlexRow}>
                              <label htmlFor="rate">Rate: </label>
                              <div className="rate-value">{rate}</div>
                            </div>
                            <input
                              type="range"
                              min="0.5"
                              max="2"
                              defaultValue="1"
                              step="0.1"
                              id="rate"
                              onChange={(event) => {
                                setRate(event.target.value);
                              }}
                            />
                          </div>
                          <div
                            className="col-6 px-5"
                            style={styleContainerRatePitch}
                          >
                            <div style={styleFlexRow}>
                              <label htmlFor="pitch">Pitch: </label>
                              <div className="pitch-value">{pitch}</div>
                            </div>
                            <input
                              type="range"
                              min="0"
                              max="2"
                              defaultValue="1"
                              step="0.1"
                              id="pitch"
                              onChange={(event) => {
                                setPitch(event.target.value);
                              }}
                            />
                          </div>
                        </div>
                        <div
                          className="col-9"
                          style={{
                            border: "4px solid #f8f9fa",
                            backgroundColor: "#f8f9fa",
                          }}
                        >
                          <label htmlFor="message">Message</label>
                          <br />
                          <textarea
                            style={{
                              width: "100%",
                              minHeight: "150px",
                              fontSize: "17px",
                              backgroundColor: "#f8f9fa",
                            }}
                            id="message"
                            name="message"
                            placeholder="Please type message you want to make speek it out."
                            rows={3}
                            value={text}
                            onChange={(event) => {
                              setText(event.target.value);
                            }}
                          />
                        </div>
                        <div className="col-3">
                          {speaking ? (
                            <button
                              className="btn btn-secondary m-2 p-2"
                              style={{ width: "100%", height: "90%" }}
                              type="button"
                              onClick={cancel}
                            >
                              Stop
                            </button>
                          ) : (
                            <button
                              className="btn btn-success m-2 p-2"
                              style={{ width: "100%", height: "90%" }}
                              type="button"
                              onClick={() =>
                                speak({ text, voice, rate, pitch })
                              }
                            >
                              Speak
                            </button>
                          )}
                        </div>
                      </div>
                    </div>
                  </React.Fragment>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
      <style jsx>{`
        .phone-container {
          display: flex;
          justify-content: center;
          align-items: center;
          height: 100vh;
          background: linear-gradient(135deg, #2d75ff, #a8bfff);
        }
        .phone {
          position: relative;
          width: 300px;
          height: 600px;
          background: #000;
          border-radius: 20px;
          overflow: hidden;
          box-shadow: 0 0 20px rgba(0, 0, 0, 0.5);
        }
        .phone-screen {
          position: relative;
          width: 100%;
          height: 80%;
          background: #fff;
          overflow-y: auto;
          padding: 20px;
        }
        .assistant-container {
          display: flex;
          justify-content: center;
          align-items: center;
          height: 100%;
        }
        .assistant-content {
          text-align: center;
        }
        .assistant-title {
          font-family: Georgia, Times, serif;
          font-size: 32px;
          font-weight: bolder;
          margin-bottom: 20px;
        }
        .assistant-body {
          margin-top: 20px;
        }
        .listening-btn {
          background-color: #f00;
          color: #fff;
          border: none;
          border-radius: 5px;
          padding: 10px;
          cursor: pointer;
          transition: background-color 0.3s ease;
        }
        .listening-btn.listening {
          background-color: #00f;
        }
      `}</style>
    </div>
  );
};

export default TextToSpeech;
