const Assistant = () => {
  const history = useHistory();
  const { speak } = useSpeechSynthesis();
  const { stopListening } = useSpeechRecognition();
  const [isListening, setIsListening] = useState(false);

  useEffect(() => {
    return () => {
      stopListening(); // Stop listening when component unmounts
    };
  }, [stopListening]);

  const startListening = () => {
    setIsListening(true);
    SpeechRecognition.startListening({ continuous: true });
  };

  const stopListeningHandler = () => {
    setIsListening(false);
    SpeechRecognition.stopListening();
  };

  const redirectHandler = (link) => {
    const x = `Redirect to: ${link}`;
    console.log(x);
    if (link === "object detection") {
      speak({ text: x });
      history.push(`/object-detection`);
    } else if (link === "face recognition") {
      speak({ text: x });
      history.push(`/face-recognition`);
    } else if (link === "image to text") {
      speak({ text: x });
      history.push(`/image-to-text`);
    } else if (link === "text to speech") {
      speak({ text: x });
      history.push(`/text-to-speech`);
    } else if (link === "speech to text") {
      speak({ text: x });
      history.push(`/speech-to-text`);
    } else if (link === "home") {
      speak({ text: x });
      history.push(`/`);
    } else {
      speak({ text: "I don't get it, Please try again!" });
    }
  };

  const commands = [
    {
      command: "Go to *",
      callback: (link) => redirectHandler(link),
    },
    {
      command: "Hey Jarvis",
      callback: () => {
        speak({ text: "Hi Sir" });
        console.log("hii");
      },
    },
  ];
  const { transcript: t1 } = useSpeechRecognition({ commands });

  return (
    <div className="phone-container">
      <div className="phone">
        <div className="phone-screen">
          <div className="assistant-container">
            <div className="assistant-content">
              <div className="assistant-title">
                <h1>Assistant</h1>
              </div>
              <div className="assistant-body">
                <h3>Say "Hey Jarvis"</h3>
                <p>{t1 ? t1 : "Start listening for transcript"}</p>
                <button
                  onClick={isListening ? stopListeningHandler : startListening}
                  className={`listening-btn ${isListening ? "listening" : ""}`}
                >
                  {isListening ? "Listening" : "Start Listening"}
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
      <style jsx>{`
        .phone-container {
          display: flex;
          justify-content: center;
          align-items: center;
          height: 100vh;
          background: linear-gradient(135deg, #2d75ff, #a8bfff);
          overflow: hidden;
        }
        .phone {
          width: 60%;
          height: 90%;
          max-width: 600px;
          max-height: 800px;
          background: #333;
          border-radius: 20px;
          overflow: hidden;
          box-shadow: 0 0 20px rgba(0, 0, 0, 0.5);
        }
        .phone-screen {
          height: 100%;
          overflow-y: auto;
          padding: 20px;
          display: flex;
          flex-direction: column;
          justify-content: center;
          align-items: center;
          background: #fff;
        }
        .assistant-container {
          width: 100%;
          max-width: 400px;
        }
        .assistant-content {
          text-align: center;
        }
        .assistant-title {
          font-size: 28px;
          margin-bottom: 20px;
          text-shadow: 1px 1px 2px #000;
        }
        .assistant-body {
          margin-top: 20px;
        }
        .listening-btn {
          background-color: #f00;
          color: #fff;
          border: none;
          border-radius: 5px;
          padding: 10px;
          cursor: pointer;
          transition: background-color 0.3s ease;
        }
        .listening-btn.listening {
          background-color: #00f;
        }
      `}</style>
    </div>
  );
};

export default Assistant;
